import * as React from 'react';
import {View, Text, Image, StyleSheet} from 'react-native' 

const img = '../assets/images/'+this.props.image;

export default class frogItem extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          image: require(img),
        };
      }
    render() {
        console.log(this.state.image);
        return(
            <View style={styles.frogs}>   
                <Image style={styles.frogImage} source={this.state.image}/>    
                <Text>{this.props.name}</Text>
            </View>
        )}
}

const styles = StyleSheet.create({
    frogs:{
        width: "100%",
        height: 200
    },
    frogImage:{
        width: 100,
        height: 100
    }
})
