import { StyleSheet, Text, View, FlatList, Image,TouchableOpacity, ScrollView } from 'react-native';
import frogs from '../data/frogs.js';

//lifecycle image source https://www.freepik.com/vectors/tadpole Tadpole vector created by macrovector

function Home(props){
    const frogData = frogs();
    return(
        <View style={styles.container}>
            <ScrollView>
                <Text>Scientify Name: <Text style={styles.science}>Anura</Text></Text>
                <View style={styles.frogContainer}>
                    <FlatList horizontal={true} 
                    data={frogData} 
                    initialScrollIndex={0.5}
                        renderItem={({ item, index, separators }) => (
                        <TouchableOpacity onPress={()=>{props.showFrog(item.id)}} key={item.id} style={styles.frogs} >   
                            <Image style={styles.frogImage} source={item.image}/>    
                            <Text style={styles.frogText}>{item.name}</Text>
                        </TouchableOpacity>
                        )}
                    />
                </View>
                <Text>Lifecycle of a Frog</Text>
                <Image source={require('../assets/images/27663.jpg')}/>
            </ScrollView>
        </View>
    );
}

export default Home;

const styles =  StyleSheet.create({
    container:{
        flex: 1,
        backgroundColor: 'white',
        color: 'black',
        justifyContent: 'flex-start',
        flexDirection: 'column',
        textAlign: 'center',
        width: '100%',
        alignItems: 'center',
    },
    science:{
        fontStyle: 'italic'
    },
    frogContainer:{
        borderTopColor: 'darkred',
        borderBottomColor: 'darkred',
        borderTopWidth: 3,
        borderBottomWidth: 3,
        height: 210,
    },
    frogs:{
        height: 200,
        padding: 5,
        margin: 5,
        justifyContent: 'center',
        alignItems: 'center'
    },
    frogImage:{
        width: "auto",
        minWidth: 150,
        maxWidth: 350,
        height: 150
    },
    frogText:{
        textAlign: 'center'
    }
});
