import {View, TouchableOpacity, Image, StyleSheet, FlatList,Text} from 'react-native'
import frogs from '../data/frogs.js';

function Species(){
    const frogData = frogs();
    const select = (item) =>{console.log(item)};
    return(
        <View style={styles.container}>
            {frogData.map((frog) => {
                return (
                <TouchableOpacity style={styles.frogs} onPress={()=>select(item.id)}>
                    <Image style={styles.frogImage} source={frog.image} />
                    <Text>{frog.name}</Text>
                </TouchableOpacity>
                );
            })}
        </View>
    )
}

export default Species;

const styles = StyleSheet.create({
    container:{
        flexDirection: 'column',
        width: '100%'
    },
    frogImage:{
        width: '100%',
        height: 100
    },
    frogs:{
        height: 200,
        padding: 5,
        margin: 5,
        justifyContent: 'center',
        alignItems: 'center'
    },
});
