import { StyleSheet, Text, View, FlatList, Image } from 'react-native';
//const frogs = require('../data/frogsData.json');
import frogs from '../data/frogs.js';

const frogData = frogs();

function Home(){
    return(
        <View style={styles.container}>
            <Text>Scientify Name: <Text style={styles.science}>Anura</Text></Text>
            <FlatList horizontal={true} data={frogData}
                 renderItem={({ item, index, separators }) => (
                    <View style={styles.frogMenu} key={item.id}>
                        <Image style={styles.frogMenuImage}
                            source={
                                {uri:item.image}
                            }
                        />
                        <Text>{item.name}</Text>
                    </View>
                 )}
            />
        </View>
    );
}

export default Home;

const styles =  StyleSheet.create({
    container:{
        flex: 1,
        backgroundColor: 'white',
        color: 'black',
        justifyContent: 'center',
        flexDirection: 'column',
        textAlign: 'center',
        width: '100%',
        alignItems: 'center'
    },
    science:{
        fontStyle: 'italic'
    },
    frogMenu:{
        width: "50%",
        textAlign: 'center'
    },
    frogMenuImage:{
        width: "100%",
        height: 200
    }
});
