import { StyleSheet, Text, View, FlatList, Image,ScrollView } from 'react-native';
import FrogItem from '../componets/frogItem';
//var frogs = require('../data/frogsData.json');
import frogs from '../data/frogs.js';



function Home(){
    const frogData = frogs();
    const Separator = '|';
    return(
        <View style={styles.container}>
            <Text>Scientify Name: <Text style={styles.science}>Anura</Text></Text>
            <FlatList horizontal={true} 
            data={frogData} 
                renderItem={({ item, index, separators }) => (
                <View key={item.id} style={styles.frogs}>   
                    <Image style={styles.frogImage} source={item.image}/>    
                    <Text>{item.name}</Text>
                </View>
                )}
            />
            
        </View>
    );
}

export default Home;

const styles =  StyleSheet.create({
    container:{
        flex: 1,
        backgroundColor: 'white',
        color: 'black',
        justifyContent: 'center',
        flexDirection: 'column',
        textAlign: 'center',
        width: '100%',
        alignItems: 'center',
    },
    science:{
        fontStyle: 'italic'
    },
    frogs:{
        width: 115,
        height: 200,
        borderColor: 'darkred',
        borderWidth: 2,
        padding: 5,
        margin: 5,
        textAlign: 'center'
    },
    frogImage:{
        width: 100,
        height: 100
    }
});
