import {View, TouchableOpacity, Image, StyleSheet, FlatList,Text} from 'react-native'
import frogs from '../data/frogs.js';

function Species(){
    const frogData = frogs();
    const select = (item) =>{console.log(item)};
    return(
        <View style={styles.container}>
            <FlatList
                numColumns={2}
                horizontal={false}
                columnWrapperStyle={{ flex: 1, justifyContent: "space-around" }}
                data={frogData} 
                    renderItem={({ item, index, separators }) => (
                    <TouchableOpacity style={styles.frogs} key={item.id} onPress={()=>select(item.id)}>
                        <Image style={styles.frogImage} source={item.image} />
                        <Text>{item.name}</Text>
                    </TouchableOpacity>
                    )}
                />
        </View>
    )
}

export default Species;

const styles = StyleSheet.create({
    container:{
        flexDirection: 'column',
        width: '100%'
    },
    frogImage:{
        width: '50%',
        height: 100
    },
    frogs:{
        height: 200,
        padding: 5,
        margin: 5,
        justifyContent: 'center',
        alignItems: 'center'
    },
});
