import { StyleSheet, Text, View, FlatList, Image } from 'react-native';
var frogs = require('../data/frogsData.json');
//import frogs from '../data/frogs.js';

//var frogData = frogs();
for(let f in frogs){
    let img = frogs[f]['image'];
    frogs[f]["imageUri"] = require(img); 
}

function Home(){

    console.log(frogs);
    return(
        <View style={styles.container}>
            <Text>Scientify Name: <Text style={styles.science}>Anura</Text></Text>
            <FlatList horizontal={true} data={frogs}
                 renderItem={({ item, index, separators }) => (
                    <View style={styles.frogs} key={item.id}>
                        <Image style={styles.frogMenuImage}
                            source={{uri:(item.imageUri)}}
                        />
                        <Text>{item.name}</Text>
                    </View>
                 )}
            />
        </View>
    );
}

export default Home;

const styles =  StyleSheet.create({
    container:{
        flex: 1,
        backgroundColor: 'white',
        color: 'black',
        justifyContent: 'center',
        flexDirection: 'column',
        textAlign: 'center',
        width: '100%',
        alignItems: 'center'
    },
    science:{
        fontStyle: 'italic'
    },
    frogMenu:{
        width: "50%",
        textAlign: 'center'
    },
    frogMenuImage:{
        width: 100,
        height: 100
    }
});
